INSERT INTO `wced_entry` VALUES (10665, "lamas, lamas1", "566", "<entry page=""566"" id=""10665""><form id=""lamas_laxmas"" lang=""ceb"">lamas, lámas<sub>1</sub></form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; b]</itype><trans> <tr>spice</tr>. </trans>
<eg><i lang=""ceb"">Las-ay ang útan ug dì lamásan,</i><trans> Vegetables are tasteless if you don’t spice them. </trans></eg>
<eg><i lang=""ceb"">Ang habubuyna ikalámas,</i><trans> Mint can be used as a spice. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[b]</itype><trans> spice s.t. not food. </trans>
<eg><i lang=""ceb"">Gilamásan sa kandidátu ang íyang diskursu ug mga kumidiya,</i><trans> The candidate spiced his speech with jokes. </trans></eg>
</sense></hom>
<entry><form id=""lamas"" lang=""ceb""><abbr expan=""lamas"">(→)</abbr></form>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> spice. </trans>
