INSERT INTO `wced_entry` VALUES (10693, "lambus", "568", "<entry page=""568"" id=""10693""><form id=""lambus"" lang=""ceb"">lambus</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A; c1]</itype><trans> strike with s.t. heavy, usually in a downward motion. </trans>
