INSERT INTO `wced_entry` VALUES (10694, "lambut", "568", "<entry page=""568"" id=""10694""><form id=""lambut"" lang=""ceb"">lambut</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A2; a2b2]</itype><trans> reach a certain amount, length, or distance. </trans>
<eg><i lang=""ceb"">Milambut ug usa ka líbu ang ílang halin,</i><trans> Their sales amounted to one thousand pesos. </trans></eg>
