INSERT INTO `wced_entry` VALUES (10696, "lambuyug, lambuyug", "568", "<entry page=""568"" id=""10696""><form id=""lambuyug_lambuxyug"" lang=""ceb"">lambuyug, lambúyug</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; ac]</itype><trans> whirl s.t. around on a string and throw it off in a distance. </trans>
<eg><i lang=""ceb"">Lambuyugun ku nang langgam nga nagbatug sa sanga,</i><trans> I’ll sling a rock at that bird perching on the branch. </trans></eg>
<eg><i lang=""ceb"">Gilambuyug níya ang láang úsà pasaplúti ang báka,</i><trans> He whirled the rope before he lassoed the cow. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[B26]</itype><trans> for attention, thought to veer off on a target. </trans>
<eg><i lang=""ceb"">Nalambuyug ang íyang mga mata sa nagkiaykíay nga babáyi,</i><trans> His gaze was diverted to the girl with the swaying hips. </trans></eg>
