INSERT INTO `wced_entry` VALUES (10697, "lamdag", "568", "<entry page=""568"" id=""10697""><form id=""lamdag"" lang=""ceb"">lamdag</form>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> <tr>bright</tr>. </trans>
<eg><i lang=""ceb"">Lamdag kaáyu ang síga sa plurisin,</i><trans> The fluorescent lamp gives a bright light. </trans></eg>
<eg><i lang=""ceb"">Ayaw pagsugà kay lamdag man,</i><trans> Don’t turn on the light because there’s enough light. </trans></eg>
<eg><i lang=""ceb"">Lamdag ug kaugmáun ang táwung kugihan,</i><trans> An industrious person has a bright future. </trans></eg>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> illumination. </trans>
<eg><i lang=""ceb"">Ang lamdag sa sugà,</i><trans> The lamp’s illumination. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> enlightenment. </trans>
<eg><i lang=""ceb"">Lamdag sa Ispiritu Santu,</i><trans> The enlightenment of the Holy Spirit. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[AB; ab]</itype><trans> give light or brightness; be, get bright or well-lighted. </trans>
<eg><i lang=""ceb"">Adlaw nga naglamdag sa kalibútan,</i><trans> The sun that gives light to the world. </trans></eg>
<eg><i lang=""ceb"">Dúnay táwu kay naglamdag mag ílang báy,</i><trans> There must be s.o. home because the house is lighted. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[A; b(1)]</itype><trans> enlighten. </trans>
<eg><i lang=""ceb"">Pagtulun-an nga milamdag sa ákung hunàhúná,</i><trans> A lesson that enlightened my mind. </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[B1; b6]</itype><trans> for a place to be stripped of plants or parts of plants. </trans>
<eg><i lang=""ceb"">Ang pagpahit sa kamaisan sa mga bábuy mauy nakalamdag (nakapalamdag) niíni,</i><trans> The pigs ate the corn plants and made the area bare. </trans></eg>
<eg><i lang=""ceb"">Gilamdágan ku sa mga káhuy sa lagwirta nga gipamutlan sa mga sanga,</i><trans> The trees in the yard looked bare to me after some of the branches were pruned. </trans></eg>
</sense></hom>
<entry><form id=""palamdag"" lang=""ceb""><abbr expan=""palamdag"">pa-</abbr></form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A; b]</itype><trans> go, put to a lighted or illuminated area. </trans>
<eg><i lang=""ceb"">Ayawg palamdag kay hiilhan ka,</i><trans> Don’t go under the light because you might be recognized. </trans></eg>
<eg><i lang=""ceb"">Palamdági ku ngari arun makítà ring ákung trabáhu,</i><trans> Give me some light here so I can see my work. </trans></eg>
</sense></hom></entry>
<entry><form id=""kalamdaxgun"" lang=""ceb""><abbr expan=""kalamdágun"">ka-un</abbr></form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> <tr>clarification</tr>. </trans>
</sense></hom></entry>
<entry><form id=""malamdaxgun"" lang=""ceb""><abbr expan=""malamdágun"">ma-un</abbr></form>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> <tr>enlightening</tr>.
