INSERT INTO `wced_entry` VALUES (10744, "lamuk", "572", "<entry page=""572"" id=""10744""><form id=""lamuk"" lang=""ceb"">lamuk</form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> <tr>mosquito</tr>. </trans>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[a4]</itype><trans> </trans>
</sense>
<sense n=""1""><number>1</number><trans> be infested with mosquitoes. </trans>
