INSERT INTO `wced_entry` VALUES (10752, "lamwas", "572", "<entry page=""572"" id=""10752""><form id=""lamwas"" lang=""ceb"">lamwas</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; b6]</itype><trans> for water to come in or spread over s.t. </trans>
<eg><i lang=""ceb"">Hápit malúnud ang sakayan nga nalamwásan sa túbig,</i><trans> The boat nearly sank because water came in it. </trans></eg>
<eg><i lang=""ceb"">Gilamwásan ang tugkaran sa túbig gíkan sa kanal,</i><trans> The water from the drainage ditch flooded our yard. </trans></eg>
</sense>
<sense n=""1a""><number>1a</number><trans> for water to run out over a container. </trans>
<eg><i lang=""ceb"">Milamwas ang túbig sa planggána,</i><trans> Water ran out over the basin. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[A; c1]</itype><trans> extend beyond a certain limit. </trans>
<eg><i lang=""ceb"">Mulamwas ang ákung tiil sa katri kay mubù ra,</i><trans> My feet spill over the end of the bed because it is too short. </trans></eg>
<eg><i lang=""ceb"">Lamwása (ilamwas) sa túhud ang sidsid sa ákung sinínà,</i><trans> Extend the hem line of my dress beyond the knees.
</trans></eg>
</sense></hom></entry>");
