INSERT INTO `wced_entry` VALUES (10757, "lanag", "573", "<entry page=""573"" id=""10757""><form id=""laxnag"" lang=""ceb"">lánag</form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> mild, not fatal poisoning. </trans>
<eg><i lang=""ceb"">Ang lánag ságad dílì makamatay, makapabúyud lang,</i><trans> Mild poisoning usually doesn’t kill. It just makes one dizzy. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[B126; b4(1)]</itype><trans> have, be affected by poisoning. </trans>
<eg><i lang=""ceb"">Nalánag ku sa amúting káhuy nga wà maáyung pagkalung-ag,</i><trans> I got food poisoning from the cassava that wasn’t prepared properly. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[B126]</itype><trans> be sickened, nauseated. </trans>
<eg><i lang=""ceb"">Nalánag ku sa báhù sa karni didtus ihawan,</i><trans> I was sickened by the smell of meat at the <corr sic=""abbatoir"">abattoir</corr>. </trans></eg>
</sense>
<sense n=""3""><number>3</number><trans> be bored to tears. </trans>
<eg><i lang=""ceb"">Nalánag ku ánang ímung isturya nga gibalikbálik,</i><trans> I am bored to tears by your story that you keep telling again and again. </trans></eg>
</sense></hom>
<entry><form id=""lanag"" lang=""ceb""><abbr expan=""lanag"">(→)</abbr></form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> s.t. to smoke (derogatory). </trans>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A; c1]</itype><trans> smoke. </trans>
<eg><i lang=""ceb"">Naglanag ka na sab nga gidid-an ka man sa duktur?</i><trans> You’re smoking again when the doctor told you not to.
</trans></eg>
</sense></hom></entry></entry>");
