INSERT INTO `wced_entry` VALUES (10779, "langas", "574", "<entry page=""574"" id=""10779""><form id=""langas"" lang=""ceb"">langas</form>
<hom role=""a""><pos>a</pos>
<sense n=""1""><number>1</number><trans> <tr>noisy</tr>. </trans>
<eg><i lang=""ceb"">Langas kaáyu ka nga magtuun,</i><trans> You are very noisy when you study. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> moving about a lot, engaging in horseplay. <pb n=""575""/></trans>
<eg><i lang=""ceb"">Langas kaáyung ikadúlug,</i><trans> He’ll bother you a lot if you sleep with him because he tosses and turns. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; b6]</itype><trans> make noise. </trans>
<eg><i lang=""ceb"">Dílì mu maglangas kay matúlug ku,</i><trans> You should not make noise because I’m going to bed. </trans></eg>
</sense>
<sense n=""1a""><number>1a</number> <itype>[A3]</itype><trans> breathe a word. </trans>
<eg><i lang=""ceb"">Dílì ka maglangas kang bísan kinsa sa ímung nakit-an,</i><trans> Don’t you tell anyone what you saw. </trans></eg>
</sense>
<sense n=""1b""><number>1b</number><trans> shut up! </trans>
<eg><i lang=""ceb"">Langas! Naglamlam ka lang!</i><trans> Shut up. You are talking nonsense! </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[A]</itype><trans> moving about, not keeping still. </trans>
<eg><i lang=""ceb"">Langas! Ayaw paghikaphíkap,</i><trans> Hold still. Keep your paws to yourself. </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[A]</itype><trans> make a fuss about s.t. </trans>
<eg><i lang=""ceb"">Mulangas man ku ug dílì bahínan,</i><trans> I will make a fuss if you do not give me a share. </trans></eg>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> <tr>noise</tr>. </trans>
</sense>
<sense n=""2""><number>2</number><trans> fuss, ado. </trans>
<eg><i lang=""ceb"">Mitrabáhu dáyun sa walay daghan nga langas,</i><trans> He went to work without much ado. </trans></eg>
</sense></hom>
<entry><form id=""langasan"" lang=""ceb""><abbr expan=""langasan"">-an</abbr></form>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> noisy, engaging in horseplay. </trans>
<eg><i lang=""ceb"">Kadtung langasan kaáyu dílì dad-un sa sini,</i><trans> Those of you who don’t keep still will not get to go to the show.
</trans></eg>
</sense></hom></entry></entry>");
