INSERT INTO `wced_entry` VALUES (10794, "langgikit", "575", "<entry page=""575"" id=""10794""><form id=""langgixkit"" lang=""ceb"">langgíkit</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; a12]</itype><trans> tie or attach things together or side by side. </trans>
<eg><i lang=""ceb"">Mulanggíkit ka ug pila ka bulus kawáyan pára gam-ung gákit,</i><trans> Tie several lengths of bamboo together to make a raft. </trans></eg>
<eg><i lang=""ceb"">Langgikítun nákù ning duha ka buuk káhuy arun makas-a ug dala,</i><trans> I will tie these two pieces of wood together so I can carry them all at once. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[C23]</itype><trans> be near one another. </trans>
<eg><i lang=""ceb"">Sígi sila nga maglanggíkit bisag ása lakaw,</i><trans> They are always near each other wherever they go. </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[A; c6]</itype><trans> involve s.o. in s.t. </trans>
<eg><i lang=""ceb"">Ang ímung binúang naglanggíkit nákù sa kaúlaw,</i><trans> Your foolishness involved me in your shame. </trans></eg>
<eg><i lang=""ceb"">Ayaw kug ilanggíkit sa inyung tinuntu,</i><trans> Do not involve me in your shenanigans. </trans></eg>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> link, means of connecting. </trans>
<eg><i lang=""ceb"">Kining suláta mauy langgíkit sa kagahápun,</i><trans> This letter is a link to the past.
</trans></eg>
</sense></hom></entry>");
