INSERT INTO `wced_entry` VALUES (10805, "langi2", "576", "<entry page=""576"" id=""10805""><form id=""langiq2"" lang=""ceb"">langì<sub>2</sub></form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A]</itype><trans> </trans>
</sense>
