INSERT INTO `wced_entry` VALUES (10807, "langis", "576", "<entry page=""576"" id=""10807""><form id=""langis"" lang=""ceb"">langis</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A2; c]</itype><trans> hoodwink s.o. into doing s.t. by giving him sweet talk. </trans>
