INSERT INTO `wced_entry` VALUES (10876, "lanug", "580", "<entry page=""580"" id=""10876""><form id=""laxnug"" lang=""ceb"">lánug</form>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> <tr>loud</tr>, <tr>resonant</tr>. </trans>
