INSERT INTO `wced_entry` VALUES (10881, "lanut", "580", "<entry page=""580"" id=""10881""><form id=""laxnut"" lang=""ceb"">lánut</form>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> <tr>hemp</tr> from abaca. </trans>
</sense>
<sense n=""2""><number>2</number><trans> any k.o. long, tough plant fibers. </trans>
<eg><i lang=""ceb"">Ánang trabahúa ang ímung kinabúhì naglatay sa usa ka lugas lánut,</i><trans> Your job is very dangerous. (Lit. In that job your life walks over a long thin strand.) </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; a12]</itype><trans> make hemp from abaca. </trans>
<eg><i lang=""ceb"">Anus-a man lanúta ninyu ang pinutul nga abaka?</i><trans> When will you make hemp from that abaca you cut into pieces? </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[B5]</itype><trans> show fibrous components, become threadbare. </trans>
<eg><i lang=""ceb"">Dáan na kaáyung kalsunísa naglanut na gánì ang panaptun,</i><trans> These trousers are very old. They are already threadbare. </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[B456]</itype><trans> for s.t. sticky like syrup, glue, and the like to show fiber-like portions when stretched. </trans>
</sense></hom>
<entry><form id=""lanuxtun"" lang=""ceb""><abbr expan=""lanútun"">-un</abbr></form>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> fibrous, having fibers. </trans>
<eg><i lang=""ceb"">Lanútun kaáyu ug unud ang páhù,</i><trans> <ix>Páhù</ix> is a stringy kind of mango. </trans></eg>
</sense></hom></entry>
<entry><form id=""lanutlanut"" lang=""ceb"">lanutlanut</form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> fine fibers of fruits.
</trans>
</sense></hom></entry></entry>");
