INSERT INTO `wced_entry` VALUES (10883, "lapad", "581", "<entry page=""581"" id=""10883""><form id=""lapad"" lang=""ceb"">lapad</form>
<hom role=""a""><pos>a</pos>
<sense n=""1""><number>1</number><trans> <tr>flat</tr>, <tr>level</tr> surface. </trans>
<eg><i lang=""ceb"">Lapad siyag nawung,</i><trans> She has a flat face. </trans></eg>
<eg><i lang=""ceb"">Lapad ug tiil,</i><trans> Flat-footed or having the toes wide apart. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> <tr>broad</tr>, having considerable width. </trans>
<eg><i lang=""ceb"">Lapad nga karitíra,</i><trans> The broad highway. </trans></eg>
<eg><i lang=""ceb"">Lapad nga kayutáan,</i><trans> Vast stretches of land. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[AB; a]</itype><trans> be flat, wide. </trans>
<eg><i lang=""ceb"">Mulapad kag baril, mauy ikural,</i><trans> Flatten barrels to make into a fence. </trans></eg>
<eg><i lang=""ceb"">Naglapad lang nang ilung mu,</i><trans> Your nose is so flat. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[APB2; ab7]</itype><trans> widen or broaden. </trans>
<eg><i lang=""ceb"">Nagkalapad na ang baul nga gihawánan na,</i><trans> The field has more area now that it has been cleared of bushes. </trans></eg>
<eg><i lang=""ceb"">Lapdan ang lamísa kay gamay ra,</i><trans> The table will be made wider because it is too narrow. </trans></eg>
</sense></hom>
