INSERT INTO `wced_entry` VALUES (11048, "law-ay", "593", "<entry page=""593"" id=""11048""><form id=""law-ay"" lang=""ceb"">law-ay</form>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> offensive to decency, revolting to the taste. </trans>
<eg><i lang=""ceb"">Gisinsur ang law-ay nga libru,</i><trans> The obscene book was censored. </trans></eg>
<eg><i lang=""ceb"">Law-ay kaáyung tan-áwun ang dakù nga magbinátà,</i><trans> It’s disgusting to see a grown-up act like a child. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[B12; b6]</itype><trans> be <tr>lewd</tr>, <tr>obscene</tr>. </trans>
<eg><i lang=""ceb"">Nagkalaw-ay ang mga sini karun,</i><trans> Movies are all lewd nowadays. </trans></eg>
<eg><i lang=""ceb"">Gilaw-ayan ku sa báhù sa patay,</i><trans> The odor of the corpse was revolting to me.
</trans></eg>
</sense></hom></entry>");
