INSERT INTO `wced_entry` VALUES (11081, "layaw", "596", "<entry page=""596"" id=""11081""><form id=""laxyaw"" lang=""ceb"">láyaw</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A2]</itype><trans> for animals to roam about. </trans>
