INSERT INTO `wced_entry` VALUES (11190, "ligid", "603", "<entry page=""603"" id=""11190""><form id=""lixgid"" lang=""ceb"">lígid</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[APB2S6; c1]</itype><trans> <tr>roll</tr>, roll s.t. </trans>
<eg><i lang=""ceb"">Si Pidru mauy mulígid (mupalígid) sa búla,</i><trans> Pedro will roll the ball. </trans></eg>
<eg><i lang=""ceb"">Lígid dáyun ug masúnug ang sinínà,</i><trans> Roll (on the ground) if your clothes are afire. </trans></eg>
<eg><i lang=""ceb"">Ligírun (ligdun, ilígid) ku kanà kay nakaáli sa ákung agiánan,</i><trans> I will roll it out of the way. </trans></eg>
</sense>
<sense n=""1a""><number>1a</number> <itype>[A23]</itype><trans> for days, years, etc. to roll by (literary). </trans>
<eg><i lang=""ceb"">Túling milígid ang katuígan,</i><trans> Years rolled by swiftly. </trans></eg>
</sense>
<sense n=""1b""><number>1b</number> <itype>[A]</itype><trans> throw a tantrum by rolling on the floor. </trans>
<eg><i lang=""ceb"">Ngánung milígid ang bátà? Unsa na puy íyang gustu?</i><trans> Why is the child throwing a tantrum? What does he want now? </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[A]</itype><trans> <tr>lie down</tr>. </trans>
<eg><i lang=""ceb"">Mulígid siya dáyun igkahumag káun,</i><trans> He lies down as soon as he finishes eating. </trans></eg>
</sense>
<sense n=""2a""><number>2a</number> <form id=""lixgid_sa_banig"" lang=""ceb""><abbr expan=""lígid"">—</abbr> sa banig</form><trans> be <tr>bedridden</tr>. </trans>
<eg><i lang=""ceb"">Tulu na ka túig siya nga naglígid sa banig,</i><trans> She has been bedridden for three years. </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[A; b6]</itype><trans> <tr>wallow</tr>. </trans>
<eg><i lang=""ceb"">Ang kábaw sa panahun sa ting-init mulígid sa tunaan,</i><trans> On hot days the carabao wallows <pb n=""604""/>in the pond. </trans></eg>
<eg><i lang=""ceb"">Kanang daginutána naglígid sa bahandì,</i><trans> That miser is wallowing in riches. </trans></eg>
<eg><i lang=""ceb"">Naglígid siya sa kasal-ánan,</i><trans> She is sinking deep in sin. </trans></eg>
</sense>
<sense n=""4""><number>4</number> <itype>[A; a1]</itype><trans> do s.t. to s.o. mercilessly without letup. </trans>
<eg><i lang=""ceb"">Gilígid kug pangutána sa abugádu,</i><trans> The lawyer cross-examined me. </trans></eg>
<eg><i lang=""ceb"">Ang asáwa gilígid ug hawuk sa íyang bána,</i><trans> The husband smothered his wife with kisses. </trans></eg>
</sense></hom>
<entry><form id=""ligid"" lang=""ceb""><abbr expan=""ligid"">(→)</abbr></form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> <tr>wheel</tr>. </trans>
</sense></hom></entry>
<entry><form id=""kaligiray"" lang=""ceb"">kaligiray</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A13]</itype><trans> <tr>roll around</tr>. </trans>
<eg><i lang=""ceb"">Nagkaligiray si Duman sa túmang kasakit,</i><trans> Duman rolled around in intense pain. </trans></eg>
</sense></hom></entry>
<entry><form id=""kaligiran"" lang=""ceb"">kaligiran</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A13]</itype><trans> roll head over heels. </trans>
<eg><i lang=""ceb"">Nagkaligiran ang táwung nadakin-as sa bakilid,</i><trans> The man slipped on the slope and tumbled down head over heels. </trans></eg>
</sense></hom></entry>
