INSERT INTO `wced_entry` VALUES (11222, "*lihug", "606", "<entry page=""606"" id=""11222""><form id=""xxlihug"" lang=""ceb"">*lihug</form>
<entry><form id=""palixhug"" lang=""ceb""><abbr expan=""palíhug"">pa-</abbr></form>
<hom>
<sense n=""1""><number>1</number><trans> <tr>please</tr>. </trans>
<eg><i lang=""ceb"">Palíhug, ikúhà akug túbig,</i><trans> Please, get me some water. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> work bee in which people are not paid for their labor. </trans>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; ac]</itype><trans> ask s.o. to do s.t. as a favor without pay. </trans>
<eg><i lang=""ceb"">Palihúgun ku siyag húlug ug suwat,</i><trans> I will ask him to do me the favor of mailing a letter. </trans></eg>
<eg><i lang=""ceb"">Dúna kuy ipalíhug nímu,</i><trans> I have a favor to ask of you. </trans></eg>
<eg><i lang=""ceb"">Ipalíhug kug kúhà ánà bisag wà kuy iháwun,</i><trans> Please do me the favor of getting it, even though I have no pig to roast (from the notion that people who are asked to do a big job without pay are generally given a good meal). </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[AN]</itype><trans> hold a work bee in which people work for free except for food and drinks. </trans>
<eg><i lang=""ceb"">Mupalíhug (mamalíhug) kug paalsa sa ákung balay,</i><trans> I’m having a work bee to move my house. </trans></eg>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> <xr lang=""ceb"" target=""#x-in-"">= <sc><abbr expan=""linihug"">-in-</abbr></sc>.</xr> </trans>
</sense>
<sense n=""2""><number>2</number><trans> <tr>favor</tr> asked. </trans>
</sense></hom></entry>
<entry><form id=""pinalihug"" lang=""ceb""><abbr expan=""pinalihug"">pina-</abbr></form>
