INSERT INTO `wced_entry` VALUES (11327, "linaw1", "614", "<entry page=""614"" id=""11327""><form id=""lixnaw"" lang=""ceb"">línaw<sub>1</sub></form>
<hom role=""a""><pos>a</pos>
<sense n=""1""><number>1</number><trans> <tr>calm</tr>, free of agitation. </trans>
<eg><i lang=""ceb"">Línaw ang dágat,</i><trans> The sea is calm. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> calm, free from excitement. </trans>
<eg><i lang=""ceb"">Línaw átung hunàhúnà ug walà tay mga suliran,</i><trans> Your mind is calm if you don’t have problems. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[B2S; b4(1)]</itype><trans> be, become calm. </trans>
<eg><i lang=""ceb"">Naglínaw ang panahun,</i><trans> The weather is calm. </trans></eg>
<eg><i lang=""ceb"">Dúgay ming naabut kay nalináwan ang sakayan,</i><trans> We arrived late because the boat was becalmed. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[B1; a12]</itype><trans> be calm, undisturbed. </trans>
<eg><i lang=""ceb"">Dílì maglínaw ang ímung tanlag,</i><trans> Your conscience will not be at peace. </trans></eg>
<eg><i lang=""ceb"">Lináwa ang ímung kaugalíngun, ayaw ug katarantar,</i><trans> Compose yourself. Don’t panic. </trans></eg>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> peace, calm. </trans>
<eg><i lang=""ceb"">Walay línaw ning panimuyúa,</i><trans> The way we’re living, there is no peace and quiet. </trans></eg>
</sense></hom>
<entry><form id=""malinaxwun"" lang=""ceb""><abbr expan=""malináwun"">ma-un</abbr></form>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> calmly, peacefully. </trans>
<eg><i lang=""ceb"">Malináwun siya nga namatay,</i><trans> He died calmly. </trans></eg>
</sense></hom></entry>
<entry><form id=""kalixnaw"" lang=""ceb""><abbr expan=""kalínaw"">ka-</abbr></form>
<hom role=""n""><pos>n</pos>
