INSERT INTO `wced_entry` VALUES (11495, "liut", "625", "<entry page=""625"" id=""11495""><form id=""lixut"" lang=""ceb"">líut</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A2; b6]</itype><trans> </trans>
</sense>
<sense n=""1""><number>1</number><trans> squeeze one’s way through. </trans>
<eg><i lang=""ceb"">Milíut siya sa daghang táwu,</i><trans> He squeezed his way through the crowd. </trans></eg>
<eg><i lang=""ceb"">Nasámad siya sa alambring íyang giliútan,</i><trans> He sustained cuts from the barbed wire he squeezed through. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> get out of a difficult spot by verbal cunning. </trans>
<eg><i lang=""ceb"">Bísag pit-un siya, makalíut pa giyug pangatarúngan,</i><trans> Even if you push him against the wall, he will manage to come up with an answer that will somehow get him out of it.
</trans></eg>
</sense></hom></entry>");
