INSERT INTO `wced_entry` VALUES (11498, "liwas", "625", "<entry page=""625"" id=""11498""><form id=""lixwas"" lang=""ceb"">líwas</form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> period after a holiday or big occasion. </trans>
<eg><i lang=""ceb"">Sa líwas sa pyista,</i><trans> After the fiesta. </trans></eg>
<eg><i lang=""ceb"">Líwas sa gíra,</i><trans> The post-war period. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[B256; b]</itype><trans> for it to get to be the time after some period. </trans>
<eg><i lang=""ceb"">Mulíwas na lang ang pasku wà giyud kuy pinaskúhan,</i><trans> Christmas will soon be over and I still haven’t received a single Christmas gift. </trans></eg>
<eg><i lang=""ceb"">Giliwásan giyud mi sa ting-ani didtu sa búkid,</i><trans> We stayed in the mountains until after the harvest season.
</trans></eg>
</sense></hom></entry>");
