INSERT INTO `wced_entry` VALUES (11534, "lubi", "628", "<entry page=""628"" id=""11534""><form id=""lubi"" lang=""ceb"">lubi</form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> <tr>coconut</tr>: <bio>Cocos nucifera</bio>. </trans>
</sense></hom>
<entry><form id=""mugbuq_pa_ang_lubi"" lang=""ceb"">mugbù pa ang <abbr expan=""lubi"">—</abbr></form>
<hom>
<sense n=""0""><trans> <tr>long ago</tr>. </trans>
</sense></hom></entry>
<entry><form id=""lubi_nga_lupixsan"" lang=""ceb""><abbr expan=""lubi"">—</abbr> nga lupísan</form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> variety with green husks. </trans>
</sense></hom></entry>
<entry><form id=""lubi_nga_ugis"" lang=""ceb""><abbr expan=""lubi"">—</abbr> nga ugis</form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> variety with whitish nuts, used in making coconut oil for medicinal purposes. </trans>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[b(1)]</itype><trans> mix milk extracted from coconut meat with s.t. </trans>
<eg><i lang=""ceb"">Nindut ang sabaw sa kinhasun ug lubihan,</i><trans> A chowder of seashells tastes good if coconut milk is added to it. </trans></eg>
</sense></hom></entry>
<entry><form id=""lubiluxbi"" lang=""ceb"">lubilúbi</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A; ab]</itype><trans> weed or clean the area around each coconut plant. </trans>
<eg><i lang=""ceb"">Ug dì nátù matibuuk paglimpiyu, lubilubíhun lang,</i><trans> If we can’t clean the whole area, just clean under each tree. </trans></eg>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> name given to various plants commonly found in coconut groves. </trans>
</sense>
<sense n=""1a""><number>1a</number><trans> k.o. shrub with no branches and reddish leaves growing at the tops. </trans>
</sense>
<sense n=""1b""><number>1b</number><trans> k.o. <tr>ground orchids</tr>, e.g. the ornamental <tr>orchid</tr>: <bio>Spathoglottis plicata</bio>. </trans>
</sense>
<sense n=""2""><number>2</number><trans> name given to k.o. palm. </trans>
</sense></hom></entry>
<entry><form id=""palubiluxbi"" lang=""ceb"">palubilúbi</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A]</itype><trans> play hard to get (from the notion that coconut meat is hard to extract from its shell). </trans>
<eg><i lang=""ceb"">Nagpalubilúbi siya ábig ákung lugítun,</i><trans> She is playing hard to get. She thinks I’m going to try to extract her from the shell. </trans></eg>
</sense></hom></entry>
<entry><form id=""kalubihan_kalubian_kalubinhan"" lang=""ceb"">kalubihan, kalubian, kalubinhan</form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> coconut grove. </trans>
</sense></hom></entry>
<entry><form id=""tagalubi"" lang=""ceb""><abbr expan=""tagalubi"">taga-</abbr></form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> a system of trading in which the merchandise, esp. home-made food preparations as cakes, etc., are bartered for mature coconuts. </trans>
