INSERT INTO `wced_entry` VALUES (11542, "lubu", "628", "<entry page=""628"" id=""11542""><form id=""luxbuq"" lang=""ceb"">lúbù</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A; a]</itype><trans> </trans>
</sense>
<sense n=""1""><number>1</number><trans> remove grains of corn from the cob. </trans>
<eg><i lang=""ceb"">Lubúa nang mais arun ipagaling,</i><trans> Remove the kernels so we can mill them. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> remove teeth, serrations, or anything protruding in a series, like the grains of corn. </trans>
