INSERT INTO `wced_entry` VALUES (11544, "lubug", "629", "<entry page=""629"" id=""11544""><form id=""luxbug"" lang=""ceb"">lúbug</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[AB2; a]</itype><trans> for liquid to get or be made murky with sediment. </trans>
<eg><i lang=""ceb"">Mulúbug (malúbug) ang subà ug mubahà,</i><trans> The river becomes muddy during floods. </trans></eg>
<eg><i lang=""ceb"">Ang nangaligúay mauy naglúbug sa subà,</i><trans> The bathers made the river all muddy. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[AB12]</itype><trans> for one’s sight to become, be made blurry. </trans>
<eg><i lang=""ceb"">Bulug sa lúhà nga milúbug sa íyang mata,</i><trans> A torrent of tears that blurred her sight. </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[A; a12]</itype><trans> make a wild disturbance so as to disrupt an activity. </trans>
<eg><i lang=""ceb"">Tagagawas ang naglúbug sa ámung parti,</i><trans> Some outsiders disrupted our party. </trans></eg>
</sense></hom>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> murky, blurred. </trans>
</sense></hom>
<entry><form id=""lubug"" lang=""ceb""><abbr expan=""lubug"">(→)</abbr></form>
<hom>
<sense n=""0""><trans> <xr lang=""ceb"" target=""#luxbug"">= <sc>lúbug</sc>, <i lang=""ceb"">a</i>.</xr> </trans>
</sense></hom></entry>
<entry><form id=""lubugun"" lang=""ceb""><abbr expan=""lubugun"">-un(→)</abbr></form>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> of a muddy, murky sort.
</trans>
</sense></hom></entry></entry>");
