INSERT INTO `wced_entry` VALUES (11549, "lubus", "629", "<entry page=""629"" id=""11549""><form id=""lubus"" lang=""ceb"">lubus</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[AN; a12]</itype><trans> acquire a supernatural ability by means of undergoing a series of difficult tasks (<ix>táhas</ix>) in preparation for it. </trans>
<eg><i lang=""ceb"">Ug mulubus (manlubus) kag kaláki kinahanglang makatúman ka sa táhas,</i><trans> If you want to acquire supernatural ability, you must be able to accomplish the series of difficult tasks. </trans></eg>
