INSERT INTO `wced_entry` VALUES (11550, "lubus1", "629", "<entry page=""629"" id=""11550""><form id=""luxbus"" lang=""ceb"">lúbus<sub>1</sub></form>
<hom role=""a""><pos>a</pos>
<sense n=""1""><number>1</number><trans> entirely of one color and texture. </trans>
<eg><i lang=""ceb"">Ang ákung irù lúbus nga putì,</i><trans> My dog is entirely white. </trans></eg>
<eg><i lang=""ceb"">Ang kulur sa bistídu lúbus kaáyung pula,</i><trans> My dress is colored a very pure red. </trans></eg>
<eg><i lang=""ceb"">Lúbus pánit ákung bág,</i><trans> My purse <pb n=""630""/>is all leather. </trans></eg>
