INSERT INTO `wced_entry` VALUES (11577, "lugdang", "632", "<entry page=""632"" id=""11577""><form id=""lugdang"" lang=""ceb"">lugdang</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A2; b6]</itype><trans> </trans>
</sense>
<sense n=""1""><number>1</number><trans> settle to the bottom of a liquid. </trans>
<eg><i lang=""ceb"">Wà makalugdang ang húgaw kay pirming gikútaw,</i><trans> The dirt would not settle because s.o. kept stirring the water. </trans></eg>
<eg><i lang=""ceb"">Dúgayng milugdang ang íyang paun kay súg man,</i><trans> It took his bait a long time to reach the bottom because there was a current. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> for results of bad action to make themselves felt. </trans>
<eg><i lang=""ceb"">Milugdang ang íyang tinuntu. Nakalabus,</i><trans> That is the fruit of his foolishness. He was sent to prison. </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[B3(1)4]</itype><trans> for tender rice grains to solidify. </trans>
<eg><i lang=""ceb"">Wà pa gánì makalugdang ang humay aníhun na nímu,</i><trans> Are you going to harvest the rice when the grains haven’t solidified yet? </trans></eg>
</sense></hom>
<entry><form id=""linugdang_nilugdang"" lang=""ceb""><abbr expan=""linugdang"">-in-</abbr>, <abbr expan=""nilugdang"">ni-</abbr></form>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> s.t. that settled. </trans>
</sense>
<sense n=""2""><number>2</number><trans> results of bad action. </trans>
<eg><i lang=""ceb"">Kanang batáa linugdang sa ímung tinuntu,</i><trans> The child is the fruit of your stupidity. </trans></eg>
</sense>
<sense n=""3""><number>3</number><trans> one’s youngest child (humorous). </trans>
</sense></hom></entry>
<entry><form id=""linugdaxngan"" lang=""ceb""><abbr expan=""linugdángan"">-in-an</abbr></form>
<hom>
<sense n=""0""><trans> <xr lang=""ceb"" target=""#x-in-"">= <sc><abbr expan=""linugdang"">-in-</abbr></sc>, <ix>1</ix>.</xr>
