INSERT INTO `wced_entry` VALUES (11600, "lugpit", "634", "<entry page=""634"" id=""11600""><form id=""lugpit"" lang=""ceb"">lugpit</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A; b]</itype><trans> pin s.t. down tightly or between two things. </trans>
<eg><i lang=""ceb"">Tabángi kug lugpit sa kúgun íring duha ka lipak,</i><trans> Help me fasten the grass thatch down with those two pieces of bamboo. </trans></eg>
<eg><i lang=""ceb"">Lugpíti (lugpiti) ang libru sa íluk,</i><trans> Hold your book tightly under your arm. </trans></eg>
<eg><i lang=""ceb"">Hilugpitan ákung tudlù pagsira nákù sa húnus,</i><trans> I caught my finger when I closed the drawer. </trans></eg>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> s.t. used to clip or pin s.t. tightly down or against s.t. </trans>
</sense>
<sense n=""2""><number>2</number><trans> chord on a guitar formed by pressing the finger straight across the strings. </trans>
</sense></hom>
<entry><form id=""palugpit"" lang=""ceb""><abbr expan=""palugpit"">pa-</abbr></form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A; ab]</itype><trans> cause s.t. to be pinned down. </trans>
<eg><i lang=""ceb"">Puting kamisadintru nga íyang gipalugpítan (gipalugpitan),</i><trans> A white shirt which he had tucked in.
</trans></eg>
</sense></hom></entry></entry>");
