INSERT INTO `wced_entry` VALUES (11602, "lugru", "634", "<entry page=""634"" id=""11602""><form id=""lugru"" lang=""ceb"">lugru</form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> advantage given in betting where the odds are not even. </trans>
<eg><i lang=""ceb"">Ang lugru tris kuntra sa ímung dus,</i><trans> The advantage is three to your two. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A; b6]</itype><trans> give advantage in betting, not said of tossing coins. </trans>
<eg><i lang=""ceb"">Lugrúhan ta sila ug singku tris arun mudáwat sila sa pusta,</i><trans> We will give them an advantage of five to three so that they will take the bet.
</trans></eg>
</sense></hom></entry>");
