INSERT INTO `wced_entry` VALUES (11603, "lugsad", "634", "<entry page=""634"" id=""11603""><form id=""lugsad"" lang=""ceb"">lugsad</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; c1]</itype><trans> get to the ground or bottom. </trans>
<eg><i lang=""ceb"">Milugsad na ang ayruplánu sa tugpahánan,</i><trans> The airplane has landed at the airport. </trans></eg>
<eg><i lang=""ceb"">Naglugsad sila samtang kami nagtungas,</i><trans> They were coming down the hill while we were going up. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[A; c]</itype><trans> bring s.t. down. </trans>
<eg><i lang=""ceb"">Adúnay mulugsad (maglugsad) sa mga abut ngadtu sa lungsud,</i><trans> S.o. will bring the produce down to the town.
</trans></eg>
</sense></hom></entry>");
