INSERT INTO `wced_entry` VALUES (11606, "lugtab", "634", "<entry page=""634"" id=""11606""><form id=""lugtab"" lang=""ceb"">lugtab</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; b5]</itype><trans> force a door open. </trans>
<eg><i lang=""ceb"">Wà nay kawatang makalugtab áning pultahána,</i><trans> No thief can break this door open. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[A; ab2]</itype><trans> cut a string or rope with a long slashing stroke of the blade. </trans>
<eg><i lang=""ceb"">Naglugtab siya sa tahì sa sinínang usbunun,</i><trans> She is cutting the stitches of the dress she has to alter. </trans></eg>
<eg><i lang=""ceb"">Lugtaba ang hukut arun malibri ang kabáyù,</i><trans> Cut the tether rope to free the horse. </trans></eg>
<eg><i lang=""ceb"">Hinglugtaban nákù ang halayhayan,</i><trans> I accidentally cut the clothesline. </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[A3P; a12]</itype><trans> deprive s.o. of his power. </trans>
<eg><i lang=""ceb"">Unsay íyang pagabuhátun arun malugtab ang gahum sa ingkantu?</i><trans> What shall he do to overcome the power of the spirit?
</trans></eg>
</sense></hom></entry>");
INSERT INTO `wced_head` (entryid, head, normalized_head, type, pos) VALUES (11606, "lugtab", "lugtab", "m", "v");
INSERT INTO `wced_entry` VALUES (11607, "lugtas", "634", "<entry page=""634"" id=""11607""><form id=""lugtas"" lang=""ceb"">lugtas</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; a]</itype><trans> break a rope, thread, string, etc. by pulling on it with force. </trans>
<eg><i lang=""ceb"">Naglugtas pa ku sa tahì sa ákung sinínà,</i><trans> I am ripping out the stitches in my dress. </trans></eg>
<eg><i lang=""ceb"">Kun dì mahúbad, lugtása lang,</i><trans> If you can’t untie it, just break it off. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[A; b4c1]</itype><trans> <tr>terminate</tr>, put an end to s.t. </trans>
<eg><i lang=""ceb"">Kaguul nga mulugtas sa ímung kinabúhì,</i><trans> Sorrows that can terminate your life. </trans></eg>
<eg><i lang=""ceb"">Lugtásun (ilugtas) ba lang nímu ang ímung mga damgu tungud lang sa usa ka babáyi?</i><trans> Will you destroy your hopes and dreams all because of a woman? </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[a12]</itype><trans> <tr>finish</tr> a task or job. </trans>
<eg><i lang=""ceb"">Kanang labhanan na lang ímung lugtásun karun.</i><trans> All you have to finish is that laundry.
</trans></eg>
</sense></hom></entry>");
