INSERT INTO `wced_entry` VALUES (11614, "lugus", "635", "<entry page=""635"" id=""11614""><form id=""luxgus"" lang=""ceb"">lúgus</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; c1]</itype><trans> <tr>force</tr>, force oneself. </trans>
<eg><i lang=""ceb"">Milúgus kug iskuyla bisag gihilantan ku,</i><trans> I forced myself to go to school even though I had a fever. </trans></eg>
<eg><i lang=""ceb"">Kinsay naglúgus ínig pasuk sa buhù bísag huut,</i><trans> Who forced this into the hole even though it’s too big? </trans></eg>
<eg><i lang=""ceb"">Ayawg ilúgus (lugsa) ímung láwas ug dì giyud mahímù,</i><trans> Don’t force your body if you can’t manage it. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[A; a2]</itype><trans> <tr>rape</tr>. </trans>
<eg><i lang=""ceb"">Gibítay ang naglúgus sa babáyi,</i><trans> The man who raped the girl was hanged. </trans></eg>
</sense></hom>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> <tr>hardly</tr>, <tr>barely</tr>. </trans>
<eg><i lang=""ceb"">Tíngug nga lúgus mabátì,</i><trans> A barely audible voice. </trans></eg>
<eg><i lang=""ceb"">Lúgus gánì ming makapalit ug bugas,</i><trans> We can scarcely afford to buy rice. </trans></eg>
</sense></hom>
<entry><form id=""lugus"" lang=""ceb""><abbr expan=""lugus"">(→)</abbr></form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A]</itype><trans> <tr>barely</tr> do s.t. under difficult odds. </trans>
<eg><i lang=""ceb"">Naglugus giyug iskuyla bísag walay kwarta,</i><trans> He managed to get through school despite his poverty. </trans></eg>
</sense></hom></entry>
<entry><form id=""linugsaxnay_lugsanay_lugsaxnay"" lang=""ceb"">linugsánay, lugsanay, lugsánay</form>
<hom>
<sense n=""0""><trans> <xr lang=""ceb"" target=""#luxgus"">= <sc>lúgus</sc>, <i lang=""ceb"">a</i>.</xr> </trans>
</sense></hom></entry>
<entry><form id=""pangluxgus"" lang=""ceb""><abbr expan=""panglúgus"">paN-</abbr></form>
