INSERT INTO `wced_entry` VALUES (11652, "lukmay", "637", "<entry page=""637"" id=""11652""><form id=""lukmay"" lang=""ceb"">lukmay</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[AB12; a12]</itype><trans> for feelings to become or be made soft such that a person is persuaded. </trans>
<eg><i lang=""ceb"">Ang íyang tíngug mulukmay sa labing tig-a nga kasingkásing,</i><trans> Her voice will soften the hardest of hearts. </trans></eg>
<eg><i lang=""ceb"">Nagkalukmay ang íyang balatían sa tantu námung hinangyù,</i><trans> Our imploring is gradually softening his feelings. </trans></eg>
