INSERT INTO `wced_entry` VALUES (11902, "luun", "654", "<entry page=""654"" id=""11902""><form id=""luxun"" lang=""ceb"">lúun</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A2C; ac]</itype><trans> </trans>
</sense>
<sense n=""1""><number>1</number><trans> share a space to occupy it. </trans>
<eg><i lang=""ceb"">Naglúun mig sakay sa kábaw,</i><trans> We rode on the carabao together. </trans></eg>
<eg><i lang=""ceb"">Dì mahímung luúnun ang duha ka himungáan sa usa ka pugaran,</i><trans> You can’t put two hens in one nest. </trans></eg>
