INSERT INTO `wced_entry` VALUES (15690, "sabaw", "836", "<entry page=""836"" id=""15690""><form id=""sabaw"" lang=""ceb"">sabaw</form>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> <tr>soup</tr>, <tr>gravy</tr>, <tr>juice</tr> given off by s.t. cooked. </trans>
