INSERT INTO `wced_entry` VALUES (15813, "sagukum", "844", "<entry page=""844"" id=""15813""><form id=""saguxkum"" lang=""ceb"">sagúkum</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A; a1]</itype><trans> </trans>
