INSERT INTO `wced_entry` VALUES (16110, "samad", "862", "<entry page=""862"" id=""16110""><form id=""saxmad"" lang=""ceb"">sámad</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; b(1)]</itype><trans> <tr>wound</tr>, <tr>hurt</tr>. </trans>
