INSERT INTO `wced_entry` VALUES (16192, "samuk", "866", "<entry page=""866"" id=""16192""><form id=""saxmuk"" lang=""ceb"">sámuk</form>
<hom role=""a""><pos>a</pos>
<sense n=""1""><number>1</number><trans> <tr>bothersome</tr>. </trans>
<eg><i lang=""ceb"">Sámuk kaáyu ning batáa nga galmun kay kanúnayng mangáyù ug túbig,</i><trans> This child is a <tr>nuisance</tr> to take care of because he’s constantly asking for water. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> <tr>crowded</tr> and <tr>chaotic</tr> with activity. </trans>
<eg><i lang=""ceb"">Sámuk kaáyu ang Tabuan ug malúnis,</i><trans> Taboan market is very <tr>busy</tr> on Mondays. </trans></eg>
<eg><i lang=""ceb"">Sámuk kaáyu ang upisína kay namalhin mi,</i><trans> The office is in an uproar now because we just moved. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A; a]</itype><trans> <tr>disturb</tr>, <tr>bother</tr>. </trans>
<eg><i lang=""ceb"">Latiguhan ang bisag kinsay musámuk dinhi,</i><trans> I’ll whip anybody that makes noise here. </trans></eg>
<eg><i lang=""ceb"">Dúna kuy isámuk nímu kadiyut,</i><trans> I have a favor to ask of you (lit. s.t. to disturb you with). </trans></eg>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> <tr>fight</tr>, <tr>disturbance</tr>. </trans>
<eg><i lang=""ceb"">Dinhay sámuk sa ámung klási; nagsumbagay,</i><trans> There was trouble in the class. S.o. was fighting. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> that which bothers, annoys. </trans>
<eg><i lang=""ceb"">Maáyu puy-an sa búkid kay way sámuk,</i><trans> The country is a nice place to live because there are no <tr>annoyances</tr>. </trans></eg>
</sense></hom>
