INSERT INTO `wced_entry` VALUES (16267, "*sangil", "869", "<entry page=""869"" id=""16267""><form id=""xxsaxngil"" lang=""ceb"">*sángil</form>
<entry><form id=""pasaxngil"" lang=""ceb""><abbr expan=""pasángil"" type=""lemma"">pa-</abbr></form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; b(1)]</itype><trans> <tr>blame</tr>, <tr>impute</tr> s.o. with a fault. </trans>
<eg><i lang=""ceb"">Siyay nagpasángil nga akuy naglábay sa irù,</i><trans> He is <tr>accusing</tr> me of stoning the dog. </trans></eg>
<eg><i lang=""ceb"">Ang húlaw gipasanginlan sa kangihit sa bugas,</i><trans> Drought was blamed for the scarcity of corn. </trans></eg>
<eg><i lang=""ceb"">Ug náay pyansa ang gipasanginlan makaguwà sa prísu,</i><trans> If bail is posted, the accused party can get out of jail. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[A; c6]</itype><trans> make excuses, pretend to do s.t. for a purpose. </trans>
<eg><i lang=""ceb"">Mupasángil ka ug pamisíta, sa tinúud naníid ka níla,</i><trans> You make a pretense of visiting them when in truth you are observing them. </trans></eg>
<eg><i lang=""ceb"">Ug mupalta ka, ipasángil lang sa labad sa úlu,</i><trans> If you want to be absent, just say you had a headache. </trans></eg>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> <tr>charge</tr>, <tr>accusation</tr>. </trans>
</sense>
<sense n=""2""><number>2</number><trans> <tr>excuse</tr> for not doing s.t. </trans>
</sense></hom></entry>
<entry><form id=""sumaxngil"" lang=""ceb""><abbr expan=""sumángil"" type=""lemma"">-um-</abbr></form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A23]</itype><trans> make or offer an excuse. </trans>
<eg><i lang=""ceb"">Walà siya katubag ug misumángil nga gidukà siya,</i><trans> He was not able to answer and made the excuse that he was drowsy. </trans></eg>
</sense></hom></entry>
<entry><form id=""pasumaxngil"" lang=""ceb"">pasumángil</form>
<hom>
<sense n=""0""><trans> = <xr lang=""ceb"" target=""#pasaxngil""><sc>pasángil</sc></xr>, <i lang=""ceb"">v2.</i>
