INSERT INTO `wced_entry` VALUES (16361, "sapak", "875", "<entry page=""875"" id=""16361""><form id=""sapak"" lang=""ceb"">sapak</form>
<hom role=""a""><pos>a</pos>
<sense n=""1""><number>1</number><trans> <tr>apt</tr>, <tr>exact</tr>, just the right one. </trans>
<eg><i lang=""ceb"">Ang ímung tubag sapak giyud kaáyu,</i><trans> You gave just the right answer. </trans></eg>
<eg><i lang=""ceb"">Sapak nga pagpangasáwa, dátù,</i><trans> A <tr>perfect</tr> marriage—a rich wife. </trans></eg>
