INSERT INTO `wced_entry` VALUES (16445, "sarang", "879", "<entry page=""879"" id=""16445""><form id=""sarang"" lang=""ceb"">sarang</form>
<hom>
<sense n=""1""><number>1</number><trans> <tr>adequate</tr>. </trans>
<eg><i lang=""ceb"">Mangítà kag trabáhu nga sarang nímu nga pangabuhían,</i><trans> Look for a job that will give you an income adequate for you to live on. </trans></eg>
<eg><i lang=""ceb"">Kaanyag nga sarang ikapanghambug,</i><trans> Beauty that is worth bragging about. </trans></eg>
