INSERT INTO `wced_entry` VALUES (16554, "sayud", "884", "<entry page=""884"" id=""16554""><form id=""saxyud"" lang=""ceb"">sáyud</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[B1256; b4(1)]</itype><trans> <tr>know</tr>; have cognizance or awareness of s.t. </trans>
<eg><i lang=""ceb"">Nasáyud aku nga usa ninyu namutbut,</i><trans> I know that one of you is telling a lie. </trans></eg>
<eg><i lang=""ceb"">Wà ku masáyud kun háin ang ílang balay,</i><trans> I don’t know where their house is. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[b(1)]</itype><trans> tell s.o. about s.t., give information, news, etc. </trans>
<eg><i lang=""ceb"">Sayran ta kang dáan nga si Binduy usa ka minyù,</i><trans> Let me inform you beforehand that Bendoy is a married man. </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[A; b]</itype><trans> hold the <ix>sáyud</ix> ceremony. </trans>
<eg><i lang=""ceb"">Human na mi makasáyud. Kasal na lay kúwang,</i><trans> We have already held the formal betrothal ceremony. Now only the wedding is left. </trans></eg>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> the ceremony held after the <ix>pamaláyi</ix> (in which the marriage proposal is made) in which the prospective groom returns to the bride’s family to find out whether or not they accept. In this ceremony all formal agreements and terms are stated, and the couple is engaged. </trans>
</sense></hom>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> be thoroughly acquainted with s.t. </trans>
<eg><i lang=""ceb"">Sáyud aku sa mga kinaíya sa mga táwu dinhi,</i><trans> I’m thoroughly acquainted with what the people here are like. </trans></eg>
</sense></hom>
<entry><form id=""pakisaxyud"" lang=""ceb""><abbr expan=""pakisáyud"" type=""lemma"">paki-</abbr></form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A13; b(1)]</itype><trans> ask information about s.t. </trans>
<eg><i lang=""ceb"">Magpakisáyud tag unsay nahitabù,</i><trans> Let’s ask what happened. </trans></eg>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> request for information. </trans>
</sense></hom></entry>
<entry><form id=""sinayuxran"" lang=""ceb"">sinayúran</form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> a girl who is engaged and has gone through the <ix>sáyud</ix> ceremony. </trans>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[B1256]</itype><trans> be formally betrothed. </trans>
</sense></hom></entry>
<entry><form id=""kasayuxran"" lang=""ceb"">kasayúran</form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> <tr>information</tr>. <pb n=""885""/></trans>
</sense></hom></entry>
<entry><form id=""masinayuxrun"" lang=""ceb"">masinayúrun</form>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> <tr>informative</tr>.
</trans>
</sense></hom></entry></entry>");
