INSERT INTO `wced_entry` VALUES (16559, "si1", "885", "<entry page=""885"" id=""16559""><form id=""si"" lang=""ceb"">si<sub>1</sub></form>
<hom>
<sense n=""0""><trans> particle before names or titles of persons used in constructions which require the nominative. </trans>
<eg><i lang=""ceb"">Si Pidru ákung ig-ágaw,</i><trans> Pedro is my cousin. </trans></eg>
<eg><i lang=""ceb"">Si Tátay,</i><trans> Father. </trans></eg>
</sense></hom>
<entry><form id=""si_kinsa"" lang=""ceb""><abbr expan=""si"" type=""lemma"">—</abbr> kinsa</form>
