INSERT INTO `wced_entry` VALUES (16600, "sibug", "887", "<entry page=""887"" id=""16600""><form id=""sixbug"" lang=""ceb"">síbug</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[AB3(1); c1P]</itype><trans> back s.t. up, <tr>move back</tr>. </trans>
<eg><i lang=""ceb"">Ug musíbug ka, matambug ka sa kanal,</i><trans> If you step backward, you will fall into the ditch. </trans></eg>
<eg><i lang=""ceb"">Kinsay nagsíbug sa lamísa?</i><trans> Who moved the table back? </trans></eg>
