INSERT INTO `wced_entry` VALUES (16661, "siguradu", "891", "<entry page=""891"" id=""16661""><form id=""siguraxdu"" lang=""ceb"">sigurádu</form>
<hom>
<sense n=""0""><trans> <tr>sure</tr>, [so-and-so] is true without a doubt. </trans>
<eg><i lang=""ceb"">Ug mugíkan ka ug alas sayis, sigurádu nga túa ka sa mga alas unsi,</i><trans> If you leave at six, you are sure to be there at about eleven o’clock. </trans></eg>
<eg><i lang=""ceb"">Dílì katuhúan nutisyáha! Sigurádu ka?</i><trans> That’s unbelievable news! Are you sure? </trans></eg>
<eg><i lang=""ceb"">‘Muadtu kas bayli rung gabíi?<corr>’</corr>—‘Sigurádu!’</i><trans> ‘Will you go to the dance tonight?’—‘Sure!’ </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[B156]</itype><trans> be sure. </trans>
<eg><i lang=""ceb"">Nakabayad kug sitinta apan nasigurádu na ang ákung aplikasiyun,</i><trans> I paid seventy, but my application is a <tr>sure thing</tr>.
</trans></eg>
</sense></hom></entry>");
