INSERT INTO `wced_entry` VALUES (16743, "sila", "895", "<entry page=""895"" id=""16743""><form id=""sila"" lang=""ceb"">sila</form>
<hom>
<sense n=""0""><trans> <tr>they</tr>. </trans>
<eg><i lang=""ceb"">Muadtu sila sa Mandáwi,</i><trans> They will go to Mandaue. </trans></eg>
</sense></hom>
<entry><form id=""sila_si_silang_name"" lang=""ceb""><abbr expan=""sila"" type=""lemma"">—</abbr> si, <abbr expan=""silang"" type=""lemma"">-ng</abbr> <r>[<ix>name</ix>]</r></form>
<hom>
<sense n=""0""><trans> [name] and his friends or family. </trans>
<eg><i lang=""ceb"">Túa na sila si (silang) Prid,</i><trans> Fred and his friends have left. </trans></eg>
</sense></hom></entry>
<entry><form id=""silahaxnun"" lang=""ceb"">silahánun</form>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> townmates. </trans>
<eg><i lang=""ceb"">Mutábang giyud si Pidru kang Husi kay silahánun ra,</i><trans> Pedro will surely help José because they are townmates. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> <tr>distant relatives</tr>. </trans>
<eg><i lang=""ceb"">Silahánun si Piduy ug Baldu kay mag-ágaw ílang isig ka apuhan,</i><trans> Pedoy and Baldy are distantly related because their grandfathers are cousins. </trans></eg>
</sense></hom></entry>
<entry><form id=""nixla"" lang=""ceb"">níla</form>
<hom>
<sense n=""1""><number>1</number><trans> <ix>gen.</ix>: of them, by them. </trans>
<eg><i lang=""ceb"">Ang awtu níla bag-u,</i><trans> Their car is new. </trans></eg>
<eg><i lang=""ceb"">Gibaligyà níla,</i><trans> They sold it. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> short for <bx>kaníla</bx>. </trans>
</sense></hom></entry>
<entry><form id=""kanixla"" lang=""ceb"">kaníla</form>
<hom>
<sense n=""0""><trans> <ix>dat.</ix>: them. </trans>
<eg><i lang=""ceb"">Wà ku kakità kaníla,</i><trans> I have not seen them. </trans></eg>
<eg><i lang=""ceb"">Kanílang duha, kinsay mas taas?</i><trans> Of the two of them, which one is taller? </trans></eg>
</sense></hom></entry>
<entry><form id=""ixla_ilaxha"" lang=""ceb"">íla, iláha</form>
<hom>
<sense n=""0""><trans> <ix>preposed gen.</ix>: their, by them. </trans>
<eg><i lang=""ceb"">Ang ílang amahan,</i><trans> Their father. </trans></eg>
<eg><i lang=""ceb"">Ílang gipalit,</i><trans> They bought it. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> thing that’s theirs. </trans>
<eg><i lang=""ceb"">Ang ákung awtu bag-u, ang íla (iláha) dáan,</i><trans> My car is new. Theirs is old. </trans></eg>
</sense>
<sense n=""3""><number>3</number><trans> their home, place. </trans>
<eg><i lang=""ceb"">Háin man ang íla?</i><trans> Where is their house? </trans></eg>
<eg><i lang=""ceb"">Adtu ku sa ílang (íla ka) Husi,</i><trans> I’m going to José’s house. </trans></eg>
</sense></hom></entry>
<entry><form id=""silasixla"" lang=""ceb"">silasíla</form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> related to each other. </trans>
<eg><i lang=""ceb"">Si Anuy ug si Badung? Ah, silasíla ra nà,</i><trans> Anoy and Badong? Oh, they are related to each other. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A13]</itype><trans> do s.t. by themselves. </trans>
<eg><i lang=""ceb"">Nagsilasíla ra sila ug pangáun, walà manawag,</i><trans> They just ate by themselves. They did not invite us. </trans></eg>
</sense></hom></entry>
<entry><form id=""ilaixla"" lang=""ceb"">ilaíla</form>
<hom>
<sense n=""0""><trans> = <xr lang=""ceb"" target=""#silasixla""><sc>silasíla</sc></xr>, <i lang=""ceb"">v</i>. </trans>
</sense></hom></entry>
<entry><form id=""ilahaxnun"" lang=""ceb"">ilahánun</form>
<hom>
<sense n=""0""><trans> = <xr lang=""ceb"" target=""#silahaxnun""><sc>silahánun</sc></xr>.
</trans>
</sense></hom></entry></entry>");
