INSERT INTO `wced_entry` VALUES (16808, "simba", "899", "<entry page=""899"" id=""16808""><form id=""simba"" lang=""ceb"">simba</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A; b]</itype><trans> </trans>
</sense>
<sense n=""1""><number>1</number><trans> go to attend church services. </trans>
<eg><i lang=""ceb"">Musimba ku ugmà,</i><trans> I will attend church services tomorrow. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> <tr>adore</tr>, regard with respect and affection. </trans>
<eg><i lang=""ceb"">Ang babáying ákung gisimba,</i><trans> The woman I adore. </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[A12; a3]</itype><trans> know s.o.’s innermost thoughts. </trans>
<eg><i lang=""ceb"">Nasimba ku na unsa ang ímung pagtan-aw kanákù,</i><trans> I can read your thoughts of what you think of me. </trans></eg>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> <tr>worship service</tr>. </trans>
<eg><i lang=""ceb"">Unsang urása ang simba?</i><trans> What time is the service? </trans></eg>
</sense></hom>
<entry><form id=""simbasimba"" lang=""ceb"">simbasimba</form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> <tr>praying mantis</tr>. </trans>
