INSERT INTO `wced_entry` VALUES (17183, "subra", "916", "<entry page=""916"" id=""17183""><form id=""subra"" lang=""ceb"">subra</form>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> in excess, <tr>too many</tr>, <tr>too much</tr>. </trans>
