INSERT INTO `wced_entry` VALUES (17209, "sudsud2", "918", "<entry page=""918"" id=""17209""><form id=""sudsud2"" lang=""ceb"">sudsud<sub>2</sub></form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; a]</itype><trans> <tr>dig up</tr> or <tr>turn up</tr> the earth by shoving with the tip of an instrument or (by animals) with the snout. </trans>
<eg><i lang=""ceb"">Sudsúra ning mga balíli,</i><trans> Clear off this grass (with a pushing motion). </trans></eg>
<eg><i lang=""ceb"">Ang gisudsúran sa bábuy nga yútà nangalúngag,</i><trans> The place where the pig had dug out the earth with its snout is full of holes. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[AN; a]</itype><trans> push sand lightly with the feet in search of crabs. </trans>
</sense>
<sense n=""3""><number>3</number> <itype>[A; a]</itype><trans> catch fish with a <ix>sudsud</ix>. </trans>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> bladed instrument with a blunt end used to remove weeds by shoving under the roots. </trans>
</sense>
<sense n=""2""><number>2</number><trans> k.o. small one-man fishing <tr>net</tr> made of coarse <ix>sinamay</ix> mounted on two poles, pushed through shallow water to catch shrimps or small fish. </trans>
</sense></hom>
<entry><form id=""panudsud"" lang=""ceb""><abbr expan=""panudsud"" type=""lemma"">paN-</abbr></form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A2]</itype><trans> go fishing with the <ix>sudsud</ix>.
